import { RANKING } from '../actions';

const INITIAL_STATE = [
  { name: '', score: 0, picture: '' },
];

const ranking = (state = INITIAL_STATE, action) => (action.type === RANKING
  ? [...state, action.payload]
  : state);

export default ranking;
